import React,{useState} from 'react';



const Collections = () => {
    const collectionList = [
        {
            name:"Africozy",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/collections/Hoodies.jpg?v=1681635498&width=535"
        },
        {
            name:"AfriGifts",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/collections/Untitled_design_34.png?v=1681591493&width=535"
        },
        {
            name:"Afritees",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/collections/TEes.jpg?v=1682099247&width=535"
        }
    ]
    return(
        <div>
            {
                collectionList.map((item,i) => (
                    <div
                        style={{
                            position:"relative",
                            float:"left",
                            width:"30vw",
                            marginLeft:"1vw",
                            left:"5vw",
                            height:"50vh",
                            backgroundImage:`url(${item.image})`,
                            backgroundRepeat:"no-repeat",
                            backgroundPosition:"center"
                        }}
                    >

                        <div style={{
                            position:"relative",
                            top:"50vh"
                        }}>
                            {item.name}
                        </div>
                    </div>
                ))
            }
            <div
                style={{
                    position:"relative",
                    top:"60vh",
                    right:"80vw",


                }}
            >
                Subscribe to our emails
            </div>
        </div>
    )

}
const Landing = () => {
    const [active, setActive] = useState("white")
    const [currentTab, setCurrentTab] = useState('Home')
    const tabList = [ 
        {
            name: 'Home',
            label:'Home',
            content:(
                <div style={{
                    position:"relative",
                    top:"40vh"
                }}>
                    <div
                        style={{
                            position:"relative",
                            fontSize:"3vw",
                            fontWeight:"bold",
                            right:"30vw",
                            bottom:"10vh",
                            marginBottom:"1vh",
                        }}
                    >
                        Collections
                    </div>
                    <Collections />
                </div>
            )
        },
        { 
            name: 'Collections',
            label:'Collections',
            content:(
                <div
                    style={{
                    position:"relative",
                    top:"40vh"
                    }}
                >
                    PING PING
                </div>
            )
        },
        { 
            name: 'Everything and Afrithings',
            label:'Everything and Afrithings',
            content:(
                <div
                    style={{
                        position:"relative",
                        top:"40vh"
                    }}
                >
                    Hello
                </div>
            )
        },
        { 
            name: 'Refunds & returns',
            label:'Refunds & returns',
            content:(
                <div
                    style={{
                        position:"relative",
                        top:"40vh"
                    }}
                >
                    Ping ping
                </div>
            )
        },
        { 
            name: 'About us',
            label:'About us',
            content:(
                <div style={{
                    position:"relative",
                    top:"40vh"
                }}>
                    HELLO
                </div>
            )
        },
    ]
    let imageLink = "https://cdn.shopify.com/s/files/1/0744/3968/8473/files/White_Logo.png?v=1681576513&width=120"
    return(
<div>
    <div
        style={{
            position:"fixed",
            top:"2vh",
            backgroundColor:"black",
            backgroundImage:`url(${imageLink})`,
            backgroundRepeat:"no-repeat",
            backgroundPosition:"center bottom",
            width:"100%",
            height:"20vh"
        }}
        >
    {
        tabList.map((tab, i) => (
                <button 
                variant="outlined"
                key={i}
                onClick={() => setCurrentTab(tab.name)}
                style={{
                    position:"relative",
                    textTransform:"none",
                    height:"3vh",
                    top:"7vh",
                    borderRadius:"2px",
                    display:"flex",
                    color:active === i ? "white" :"black",
                    background:"white",
                    marginLeft:"11vw",
                    float:"left"
                }}>
                    {tab.label}
                </button>

            ))
        }
    </div>
    {
        tabList.map((tab, i) => {
            if(tab.name === currentTab){
                return <div key={i}> {tab.content} </div>
            }else return
        })
    }
</div>
    )
}
export default Landing;
