import client from '../util/shopify-client'
import './style.css'

const ItemList = () => {
    const [stash, setStash] = useState([0]);
    let limit =  4;

    client.product.fetchAll().then((products) => {
        setStash(products)       
    })    

    const pingList = client.product.fetchAll().then((listings) => {
        listings.map((item,i) => {
           {item.media.map((item,i) => (
            <div>
                {item.previewImage.url}
            </div>
            ))} 
        })
    }) 




    return (
        <div>
            {
                stash.map((item,i) => (
                    <div key={i} className="productContainer">
                        <div>
                            {item.title}
                        </div>

                    </div>
                ))
            }
            Hello hello
        </div>
    )
}



const Store = () => {
    return(
        <div>
            <ItemList />
        </div>
    )
}

export default Store;
