import React from 'react';
import {RefundPhrase} from '../util/phrases'
import Footer from './Footer';

const Refund = () => {

    const Policy = [
        {
            header:"Refund Policy",
            content:RefundPhrase
        }
    ]
    return(
        <div>
            {Policy.map((item,i) => (
                <div style={{
                    position:"relative",
                    fontSize:"2vw",
                    fontWeight:"800"
                }}>
                    {item.header}
                </div>
            ))}
            <Footer />
        </div>
    )
}

export default Refund;
