import React from 'react';
import { Aboutphrase } from '../util/phrases';

const aboutHeader = [
    {
        header:"ABOUT AFRITHINGS",
        content:Aboutphrase
    }
]
const About = () => {
    return(
    <div>
        {
            aboutHeader.map((item,i) => (
                <div>
                <div
                    style={{
                        position:"relative",
                        fontSize:"2vw",
                        bottom:"25vh",
                        left:"4vw",
                        width:"20vw",
                        textAlign:"justify"
                    }}
                >
                    {item.header}
                </div>
                    <div style={{
                        position:"relative",
                        color:"white",
                        textAlign:"justify",
                        bottom:"20vh",
                        width:"80vw",
                        left:"10vw",
                        fontSize:"1.2vw"
                    }}>
                        {item.content}
                    </div>
                </div>
            ))
        }
    </div>
    )
}

export default About;
