import React from 'react';

const Home = () => {
    const homeExpo = [
        {
            name:"Bold African Print Hoodie",
            tag:"$65.00",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/products/hoodie-mockup-featuring-a-disappointed-man-m20987.png?v=1682092732&width=1100"
        },
        {
            name:"AfriGifts",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/tee-mockup-of-a-girl-wearing-hoop-earrings-standing-in-front-of-a-brick-wall-24091.png?v=1682099514&width=990"
        },
        {
            name:"Adorn Africa",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/collections/TEes.jpg?v=1682099247&width=535"
        },

    ]


    const footerList = [
        {
            name:"Powered by Shopify"
        },
        {
            name:"Refund policy"
        },
        {
            name:"Terms of service"
        },
        {
            name:"Shipping Policy"
        }
    ]
    return(
        <div>
            <div
                style={{
                    position:"relative",
                    fontSize:"3vw",
                    fontWeight:"bold",
                    right:"37vw",
                    bottom:"30vh",
                    marginBottom:"1vh",
                }}
                >
                        Collections
                    </div>
            {
                homeExpo.map((item,i) => (
                    <div
                        style={{
                            position:"relative",
                            float:"left",
                            width:"30vw",
                            marginLeft:"1vw",
                            left:"5vw",
                            height:"50vh",
                            bottom:"20vh",

                        }}

                    >
                       <div>
                            <img style={{
                                position:"relative",
                                height:"45vh",
                                width:"20vw",
                            }} src={item.image} />
                        </div>
                        <div style={{
                            position:"relative",
                            top:"50vh"
                        }}>
                            {item.name}
                        </div>
                    </div>
                ))
            }

            <div
                style={{
                    position:"relative",
                    fontSize:"2vw",
                    top:"2vh",
                    left:"5vw",
                    height:"70vh",
                    width:"20vw"
                }}
            >
                        Subsribe to our emails
                        <div>
                            <input style={{position:"relative",width:"20vw",height:"5vh"}} type='text' />
                        </div>

                        <div
                            style={{
                                position:"relative",
                                fontSize:"1vw",
                                bottom:"10vh",
                                left:"70vw",
                                backgroundColor:"#7F68E9",
                                height:"10vh",
                                borderRadius:"8vw"
                            }}
                        >
                            <img style={{
                                position:"relative",
                                top:"3vh",
                                right:"5vw",
                                height:"4vh",
                                width:"2vw"
                            }} src={"https://external-content.duckduckgo.com/iu/?u=http%3A%2F%2Fsguru.org%2Fwp-content%2Fuploads%2F2018%2F01%2Flogo-app-pictures-photo-network-instagram-social-icon.png&f=1&nofb=1&ipt=47097cc086026cd92e5a6329804b83c7fce47bf287d08e4b44094daae7c39f39&ipo=images"} />
                            <div style={{
                                position:"relative",
                                bottom:"0.5vh",
                                color:"white",
                                fontWeight:"800"
                            }}>
                                Follow shop
                            </div>
                        </div>
                    <hr style={{
                        position:"relative",
                        bottom:"8vh",
                        width:"90vw",
                        height:"0vw"
                    }} />
                <div
                    style={{
                        position:"relative",
                        fontSize:"1vw",
                        width:"100vw",
                        bottom:"5vh",
                        left:"5vw",
                    }}
                >
                    {footerList.map((item,i) => (
                        <div style={{
                            position:"relative",
                            float:"left",
                            marginLeft:"1vw"
                        }}>
                            {item.name}
                        </div>
                    ))}

                </div>
            </div>
        </div>
    )

}

export default Home;
