import React,{useEffect, useState} from 'react';
import client from '../util/shopify-client'
import './style.css'





const ItemList = () => {
    const [stash, setStash] = useState([]);
    const [source, setSource] = useState([]);
    const [itemId, setItemId]= useState();
    const [currentProduct, setcurrentProduct] = useState([])
    const [showProduct, setShowProduct] = useState()


    useEffect(()=> {
    },[])

    client.product.fetchAll().then((products) => {
        setStash(products)       
    }) 


    const HandleProduct = async () => {
        client.product.fetch(itemId).then((product) => {
            setcurrentProduct(product)
        })
        console.log("Selected item",itemId)
        if (currentProduct.length > 0){
            console.log("current product details is", currentProduct)
        }else{
            console.log("populating list",currentProduct)
        }
        return(
            <div style={{
                backgroundColor:"red"
            }}>
                Hello there
            </div>
        )
    } 
    let myArray = [currentProduct]

    const ShowProduct = () => {
        return (
            <div style={{
                position:"relative",
                backgroundColor:"red",
                width:"90vw",
                top:"20vh",
                left:"5vw",
                height:"100vh"
            }}>
                <div
                    style={{
                        position:"relative",
                        right:"10vw",
                        top:"10vh",
                        cursor:"pointer"
                    }}
                    onClick={setShowProduct(!showProduct)}
                > X </div>
                {myArray.map((item,i) => (
                    <div>
                        {item.id}
                    </div>
                ))}
            </div>
        )
    }


    return (
        <div className='storeContainer'>
            {
                stash.map((item,i) => (
                    <div onClick={() => {
                        setItemId(item.id)
                        HandleProduct()
                        setShowProduct(!showProduct)
                    }} key={i} className="productContainer">
                        <div key={i}>
                            {item.images.map((item,i) => (
                                <div key={i}>
                                    <img src={item.src} className='productImage' />
                                </div>
                            ))}
                        </div>
                        <div className="productTitle">
                            {item.title}
                        </div>
                    </div>
                ))
            }
            {showProduct ? <ShowProduct /> : null }
        </div>
    )
}



const Store = () => {
    return(
        <div>
            <ItemList />
        </div>
    )
}

export default Store;
