import React,{useState} from 'react';
import client from '../util/shopify-client'




const ItemList = () => {
    let myItems;

    myItems = client.product.fetchAll().then((products) => (
        products.map((item,i) => (
            <div>
                {item.description}
            </div>
        ))    
    ))
    return (
        myItems
    )
}

const Products = () => {

    return(

        <div>
            <ItemList />
        </div>
    )
}

const Store = () => {
    return(
        <div style={{
            position:"relative",
            top:"20vh"
        }}>
            Hello there and welcome
            <Products />
        </div>
    )
}

export default Store;
