import React from 'react';
import Footer from './Footer';
import {Celebrating,StorySoFar} from '../util/phrases'
const EverythingAfri = () => {
    const catalog = [
        {
            header:Celebrating,
            content:StorySoFar,
            date:"April 15, 2023"
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/Black_logo_480x480.png?v=1681576556"
        },

    ]
    return(
        <div>
            <div>
                {catalog.map((item,i)=> (
                    <div>
                        <img style={{
                        position:"relative",
                        float:"left",
                        width:"20vw",
                        height:"20vh",

                    }} src={item.image} />
                    <div style={{
                        position:"relative",
                        fontSize:"2vw",
                        fontWeight:"bold",
                        bottom:"35vh"
                    }}>
                        {item.header}
                    </div>

                    <div style={{
                        position:"relative",
                        width:"92vw",
                        fontSize:"1.5vw",
                        textAlign:"left",
                        bottom:"29vh",
                        left:"4vw"
                    }}>
                        {item.content}
                    </div>
                    <div>
                </div>
                </div>
                ))}
            </div>
            <Footer />
        </div>
    )
}

export default EverythingAfri;
