import React from 'react';
import {RefundPhrase} from '../util/phrases'
import Footer from './Footer';
import "animate.css/animate.min.css";
import { AnimationOnScroll } from 'react-animation-on-scroll';

const Refund = () => {

    const Policy = [
        {
            header:"Refund Policy",
            content:<AnimationOnScroll>{RefundPhrase}</AnimationOnScroll>
        }
    ]
    return(
        <div>
            {Policy.map((item,i) => (
            <div style={{
                position:"relative",
                bottom:"35vh"
            }}>
                <div style={{
                    position:"relative",
                    fontSize:"2vw",
                    fontWeight:"800"
                }}>
                    {item.header}
                </div>
                <div
                    style={{
                        position:"relative",
                        fontSize:"1.5vw",
                        top:"5vh",
                        left:"5vw",
                        width:"92vw",
                        textAlign:"justify"
                    }}
                >
                        {item.content}
                </div>

            </div>
            ))}
            <Footer />
        </div>
    )
}

export default Refund;
