import React from 'react';
import { phrase1 } from '../util/phrases';
import {Collections} from './Landing'

const Home = () => {
    const homeExpo = [
        {
            name:"Bold African Print Hoodie",
            tag:"$65.00",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/products/hoodie-mockup-featuring-a-disappointed-man-m20987.png?v=1682092732&width=1100"
        },
        {
            name:"Wear Africa",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/tee-mockup-of-a-girl-wearing-hoop-earrings-standing-in-front-of-a-brick-wall-24091.png?v=1682099514&width=990"
        },
        {
            name:"AfriCozy",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/collections/Hoodies.jpg?v=1681635498&width=535"
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/Black_logo_480x480.png?v=1681576556"
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/NB_5_Hoodie_6_480x480.png?v=1681595215"
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/NB_5_Hoodie_7_160x160.png?v=1681595717"
        }

    ]

    const giftShop = [
        {
            name:"AfriGifts",
            description:phrase1
        },
        {
            name:"",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/products/51204946e633d282191c48bc564a1447.jpg?v=1681561234&width=360",
        },
        {
            name:"",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/products/e96029ec9cd2ff571f1eb5b14eb818a6.jpg?v=1681561209&width=360",
        },
        {
            name:"",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/products/e769de6e163cde3d73e42ea0aeef75be.jpg?v=1681561180&width=360"
        },
        {
            name:"",
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/products/9769b3978e8650626495156b600e05cd.jpg?v=1681561158&width=360"
        }
    ]

    const footerList = [
        {
            name:"Powered by Shopify"
        },
        {
            name:"Refund policy"
        },
        {
            name:"Terms of service"
        },
        {
            name:"Shipping Policy"
        }
    ]

    return(
        <div>
            <div
                style={{
                    position:"relative",
                    fontSize:"3vw",
                    fontWeight:"bold",
                    textAlign:"center",
                    bottom:"30vh",
                    marginBottom:"1vh",

                }}
                >
                        Adorn Africa
                    </div>
            {
                homeExpo.map((item,i) => (
                    <div
                        style={{
                            position:"relative",
                            float:"left",
                            width:"30vw",
                            left:"5vw",
                            height:"50vh",
                            bottom:"20vh",

                        }}

                    >
                       <div>
                            <img style={{
                                position:"relative",
                                height:"55vh",
                                width:"25vw",
                                borderRadius:"1vw"
                            }} src={item.image} />
                            <div style={{
                                position:"relative",
                                top:"5vh"
                            }}>
                                {item.name}
                            </div>
                        </div>
                    </div>
                ))
            }
            {giftShop.map((item,i) => (
                <div style={{
                    position:"relative",
                    textAlign:"center",
                    top:"50vh",
                    backgroundColor:"black"
                }}>
                    <div style={{
                        position:"relative",
                        textAlign:"center",
                        color:"white",
                        bottom:"50vh",
                        fontWeight:"800",
                        fontSize:"3vw",
                        color:"white"

                    }}>
                        {item.name}
                    </div>
                    <div
                        style={{
                            position:"relative",
                            color:"white",
                            textAlign:"justify",
                            bottom:"45vh",
                            width:"80vw",
                            left:"10vw",
                            fontSize:"1.2vw",
                        }}
                    >
                        {item.description}
                    </div>
                    <div >
                       <img style={{
                            position:"relative",
                            float:"left",
                            left:"10vw",
                            height:"35vh",
                            backgroundColor:"red",
                            bottom:"40vh",
                            marginLeft:"2vw",
                            borderRadius:"1vw"
                       }} src={item.image} />
                    </div>

                </div>
            ))}
            <div style={{
                position:"relative",
                top:"50vh"
            }}>
                <div
                     style={{
                         position:"relative",
                         fontSize:"3vw",
                         fontWeight:"bold",
                         right:"40vw",
                         top:"4vh",

                    }}
                >
                        Collections
                </div>
                <Collections />
            </div>
        </div>
    )

}

export default Home;
