import React,{useState} from 'react';



const Collections = () => {
    const collectionList = [
        {
            name:"Africozy",
            image:"https://www.afrithings.com/collections/frontpage"
        },
        {
            name:"AfriGifts",
            image:"https://www.afrithings.com/collections/afrigrifts"
        },
        {
            name:"Afritees",
            image:"https://www.afrithings.com/collections/tee-shirts"
        }
    ]
        let collectionItem = 

            <div>
                {collectionList.map((item,i)=>(
                    <div
                        style={{
                            backgroundImage:`url(${item.image})`
                        }}
                    >
                        {item.name}
                    </div>
                ))}
            </div>

}
const Landing = () => {
    const [active, setActive] = useState("white")
    const [currentTab, setCurrentTab] = useState('Home')
    const tabList = [ 
        {
            name: 'Home',
            label:'Home',
        },
        { 
            name: 'Collections',
            label:'Collections',
            content:(
                <div>
                    <Collections />
                </div>
            )
        },
        { 
            name: 'Everything and Afrithings',
            label:'Everything and Afrithings',
            content:(
                <div>
                    Hello
                </div>
            )
        },
        { 
            name: 'Refunds & returns',
            label:'Refunds & returns',
            content:(
                <div>
                    Ping ping
                </div>
            )
        },
        { 
            name: 'About us',
            label:'About us',
            content:(
                <div>
                    HELLO
                </div>
            )
        },
    ]
    let SelectItem = 
    <div
        style={{
            position:"fixed",
            top:"2vh",
            background:"black",
            backgroundImage:`url("https://cdn.shopify.com/s/files/1/0744/3968/8473/files/White_Logo.png?v=1681576513&width=120")`,
            backgroundRepeat:"no-repeat",
            backgroundPosition:"center bottom",
            width:"100%",
            height:"20vh"
        }}
        >
    {
        tabList.map((tab, i) => (
                <button 
                variant="outlined"
                key={i}
                onClick={() => setCurrentTab(tab.name)}
                style={{
                    position:"relative",
                    textTransform:"none",
                    height:"3vh",
                    top:"7vh",

                    borderRadius:"2px",
                    display:"flex",
                    color:active === i ? "white" :"black",
                    background:"white",
                    marginLeft:"11vw",
                    float:"left"
                }}>
                    {tab.label}
                </button>

            ))
    }
    </div>
    {
        tabList.map((tab,i) => {
            if(tab.name === currentTab){
                return <div key={i}>{tab.content}</div>
            }
        })
    }

    return(
        <div>
            {SelectItem}
        </div>
    )
}
export default Landing;
