import React,{useState} from 'react';
const Landing = () => {
    const [active, setActive] = useState("white")
    const tabList = [ 
        {
            name: 'Home',
            label:'Home',
        },
        { 
            name: 'Collections',
            label:'Collections',
        },
        { 
            name: 'About us',
            label:'About us',
        },
        { 
            name: 'Refunds & returns',
            label:'Refunds & returns',
        },
        { 
            name: 'Everything and Afrithings',
            label:'Everything and Afrithings',
        },
    ]
    let SelectItem = 
    <div
        style={{
            position:"fixed",
            top:"2vh",
            left:"15vw",
            background:"black",
            width:"60vw",
            height:"20vh"
        }}
        >
    {
        tabList.map((tab, i) => (
            <div
                style={{
                    backgroundImage:`url("https://cdn.shopify.com/s/files/1/0744/3968/8473/files/White_Logo.png?v=1681576513&width=120")`,
                    backgroundRepeat:"no-repeat",
                }}

                >


                <button 
                key={i}
                style={{
                    position:"relative",
                    textTransform:"none",
                    height:"3vh",
                    top:"7vh",
                    border:"none",
                    borderRadius:"2px",
                    display:"flex",
                    color:active === i ? "white" :"black",
                    background:"white",
                    marginLeft:"5vw",
                    float:"left"
                }}>
                    {tab.label}
                </button>
                </div>
            ))
    }
    </div>
    return(
        <div>
            {SelectItem}
        </div>
    )
}
export default Landing;
