import React from 'react';
import Footer from './Footer';
import {Celebrating,StorySoFar} from '../util/phrases'
const EverythingAfri = () => {
    const catalog = [
        {
            header:Celebrating,
            content:StorySoFar,
            date:"April 15, 2023"
        }
    ]
    return(
        <div>
            <div>
                {catalog.map((item,i)=> (
                    <div>
                    <div style={{
                        position:"relative",
                        fontSize:"2vw",
                        fontWeight:"bold",
                        bottom:"35vh"
                    }}>
                        {item.header}
                    </div>
                    <div style={{
                        position:"relative",
                        fontSize:"1.5vw",
                        right:"40vw",
                        bottom:"30vh",
                        fontWeight:"bold"
                    }}>
                        Our Story so far ....
                    </div>
                    <div style={{
                        position:"relative",
                        width:"80vw",
                        bottom:"28vh"
                    }}>
                        {item.content}
                    </div>
                </div>
                ))}
            </div>
            <Footer />
        </div>
    )
}

export default EverythingAfri;
