import React,{useState} from 'react';
import client from '../util/shopify-client'
import './style.css'

const ItemList = () => {
    const [stash, setStash] = useState([]);
    client.product.fetchAll().then((products) => {
        setStash(products)    
    })    
    console.log()
    return (
        <div>
            {
                stash.map((item,i) => (

                    <div className="productContainer">
                        <div>
                            {item.description}
                        </div>
                        {item.image.map((stem,i) => (
                            <div>
                                {stem.src}
                             </div>
                        ))}
                    </div>
                ))
            }
            Hello hello
        </div>
    )
}



const Store = () => {
    return(
        <div>
            <ItemList />
        </div>
    )
}

export default Store;
