import React,{useEffect, useState} from 'react';
import client from '../util/shopify-client'
import './style.css'





const ItemList = () => {
    const [stash, setStash] = useState([]);
    const [source, setSource] = useState([]);
    const [itemId, setItemId]= useState();
    const [currentProduct, setcurrentProduct] = useState([])
    const [showProduct, setShowProduct] = useState(false)


    useEffect(()=> {
    },[])

    client.product.fetchAll().then((products) => {
        setStash(products)       
    }) 

    client.checkout.create().then((checkout) => {
        console.log(checkout)
    })

    const HandleProduct = async () => {
        client.product.fetch(itemId).then((product) => {
            setcurrentProduct(product)
        })
    } 

    let myArray = [currentProduct]

    const ChangeProduct = (itemId) => {
        setItemId(itemId)
    }
    const toggle = () => {
        setShowProduct(!showProduct)
        console.log(currentProduct)
    }
    const ShowProduct = () => {
        return (
            <div className='desContainer'>
                {myArray.map((item,i) =>  {
                if(item.id === itemId){
                    return(
                    <div key={i}>
                        <div className='desTitle'> 
                            {item.title}
                        </div>
                        <div style={{
                            position:"relative",
                            width:"35vw",
                            lineHeight:"1.9vw",
                            left:"45vw",
                            fontSize:"1.2vw",
                            bottom:"30vh",

                        }}>
                            {item.description}
                        </div>
                        {item.images.map((item,i) => (
                            <div key={i}>
                                <img src={item.src} style={{position:"relative",bottom:"75vh",right:"25vw"}}/>
                            </div>
                        ))}
                        <div style={{
                             position:"relative",
                             bottom:"50vh",
                             left:"55vw",
                             fontSize:"2vw",
                             backgroundColor:"black",
                             color:"white",
                             width:"20vw",
                             borderRadius:"2vw"
                        }}>
                        <div style={{
                            position:"relative",
                            textAlign:"left",
                            marginLeft:"3vw"
                        }}>
                            Size     
                        </div>
                    </div>
                        <div 
                            style={{
                                position:"relative",
                                bottom:"65vh",
                                right:"22vw"
                            }}>
                         <div style={{
                            position:"relative",
                            right:"10vw",
                            top:"2.5vh"
                            }}>
                            Quantity
                        </div>
                        <div>
                            <input type='text' placeholder={"adjust quantity"}  />
                        </div>
                </div>
            </div>       
                    )                     
                }
            })}  


            </div>
        )
    }


    return (
        <div className='storeContainer'>
            {
                stash.map((item,i) => (
                    <div key={i} className="productContainer"
                        onClick={() => {
                            setItemId(item.id)
                            HandleProduct()
                            toggle()
                        }}
                    >
                        <div key={i}>
                            {item.images.map((item,i) => (
                                <div key={i}>
                                    <img src={item.src} className='productImage' />
                                </div>
                            ))}
                        </div>
                        <div className="productTitle">
                            {item.title}
                        </div>
                    </div>
                ))
            }
            {showProduct ? <ShowProduct /> : null }
        </div>
    )
}



const Store = () => {
    return(
        <div>
            <ItemList />
        </div>
    )
}

export default Store;
