import React from 'react';
import { Aboutphrase } from '../util/phrases';
import Footer from './Footer';
import { AnimationOnScroll } from 'react-animation-on-scroll';
const aboutHeader = [
    {
        header:"ABOUT AFRITHINGS",
        content:Aboutphrase
    }
]
const About = () => {
    return(
    <div>
        {
            aboutHeader.map((item,i) => (
                <div>
                <AnimationOnScroll animateIn='animate__fadeInLeftBig'>
                <div
                    style={{
                        position:"relative",
                        fontSize:"2vw",
                        bottom:"25vh",
                        left:"4vw",
                        width:"20vw",
                        textAlign:"justify"
                    }}
                >
                        {item.header}
                </div>
                    <div style={{
                        position:"relative",
                        color:"black",
                        marginTop:"2vw",
                        textAlign:"justify",
                        bottom:"20vh",
                        width:"80vw",
                        left:"10vw",
                        fontSize:"1.2vw"
                    }}>
                        {item.content}
                    </div>
                </AnimationOnScroll>
                </div>
            ))
        }
        <Footer />
    </div>
    )
}

export default About;
