import React,{useState} from 'react'
import { Button,ButtonGroup } from '@material-ui/core'
import client from '../util/shopify-client'

const ShowProduct = () => {
    const [source, setSource] = useState([]);
    const [itemId, setItemId]= useState();
    const [currentProduct, setcurrentProduct] = useState([])
    const [checkoutId, setCheckoutId] = useState("")
    const [counter, setCounter] = useState(0);
    const [productId, setProductId] = useState("")



const ItemstoAdd = [
    {
        productId:currentProduct,
        quantity:counter
    }
]

const HandleProduct = async () => {
    client.product.fetch(itemId).then((product) => {
        setcurrentProduct(product)
    })
} 
let myArray = [currentProduct]

const ChangeProduct = (itemId) => {
    setItemId(itemId)
}


 client.checkout.create().then((checkout) => {
    setCheckoutId(checkout.id)
  });

const Addproduct = () => {
    client.checkout.addLineItems(checkoutId,ItemstoAdd).then((checkout) => {
        console.log(checkout.lineItems)
    })
} 


    return (
        <div className='desContainer'>
            {myArray.map((item,i) =>  {
            if(item.id === itemId){
                setProductId(item.id)
                return(
                <div key={i}>
                    <div className='desTitle'> 
                        {item.title}
                    </div>
                    <div style={{
                        position:"relative",
                        width:"35vw",
                        lineHeight:"1.9vw",
                        left:"45vw",
                        fontSize:"1.2vw",
                        bottom:"30vh",

                    }}>
                        {item.description}
                    </div>
                    {item.images.map((item,i) => (
                        <div key={i}>
                            <img src={item.src} style={{position:"relative",bottom:"65vh",right:"25vw"}}/>
                        </div>
                    ))}
                    <div style={{
                         position:"relative",
                         bottom:"50vh",
                         left:"55vw",
                         fontSize:"2vw",
                         backgroundColor:"black",
                         color:"white",
                         width:"20vw",
                         borderRadius:"2vw"
                    }}>
                    <div style={{
                        position:"relative",
                        textAlign:"left",
                        marginLeft:"3vw"
                    }}>
                        Size     
                    </div>
                </div>
                    <div 
                        style={{
                            position:"relative",
                            bottom:"65vh",
                            right:"22vw"
                        }}>
                     <div style={{
                        position:"relative",
                        right:"10vw",
                        top:"4vh"
                        }}>
                        Quantity
                    </div>
                        <div style={{
                            position:"relative",
                            top:"1vh",
                            left:"48vw",
                            height:"4vh",
                            width:"8vw",
                            backgroundColor:"black",
                            color:"white",
                            borderRadius:"1vw",
                        }}>
                            <div
                                style={{
                                    cursor:"pointer",
                                    position:"relative",
                                    top:"0.5vh"
                                }}
                            >Checkout</div>
                        </div>
                    <div
                        style={{
                            position:"relative",
                            bottom:"3vh",
                            right:"3.5vw"
                        }}
                    >    
                    <ButtonGroup size="small" aria-label="small outlined button group">
                        <Button onClick={()=> 
                            {setCounter(counter+1)}}>+</Button>
                            {<Button disabled>{counter}</Button>}
                            {<Button disabled={counter <= 0} onClick={() => {
                        setCounter(counter - 1)
                    }}>-</Button>}
                    </ButtonGroup>
                    </div>
            </div>
        </div>   
             )                     
            }
        })}                             
        </div>
    )
}
export default ShowProduct;
