import React,{useEffect, useState} from 'react';
import client from '../util/shopify-client'
import './style.css'
import {ButtonGroup,Button} from '@material-ui/core'
import ShowProduct from './ShowProduct';

const ItemList = () => {
    const [stash, setStash] = useState([]);
    const [source, setSource] = useState([]);
    const [showProduct, setShowProduct] = useState(false)
    const [itemId, setItemId]= useState();

    useEffect(()=> {
        client.product.fetchAll().then((products) => {
            setStash(products)       
        }) 

    },[stash])
    /*
    const ItemstoAdd = [
        {
            productId:currentProduct,
            quantity:counter
        }
    ]
    */

    const HandleProduct = async () => {
        client.product.fetch(itemId).then((product) => {
            setcurrentProduct(product)
        })
    } 
    const toggle = () => {
        setShowProduct(!showProduct)  
    }

    /*
    const Addproduct = () => {
        client.checkout.addLineItems(checkoutId,ItemstoAdd).then((checkout) => {
            console.log(checkout.lineItems)
        })
    } */

    return (
        <div className='storeContainer'>
            {
                stash.map((item,i) => (
                    <div key={i} className="productContainer"
                        onClick={() => {
                            setItemId(item.id)
                            HandleProduct()
                            toggle()
                        }}
                    >
                        <div key={i}>
                            {item.images.map((item,i) => (
                                <div key={i}>
                                    <img src={item.src} className='productImage' />
                                </div>
                            ))}
                        </div>
                        <div className="productTitle">
                            {item.title}
                        </div>
                    </div>
                ))
            }
            {showProduct ? <ShowProduct /> : null }
        </div>
    )
}



const Store = () => {
    return(
        <div>
            <ItemList />
        </div>
    )
}

export default Store;
