import React from 'react';
import {RefundPhrase} from '../util/phrases'
import Footer from './Footer';

const Refund = () => {

    const Policy = [
        {
            header:"Refund Policy",
            content:RefundPhrase
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/Black_logo_480x480.png?v=1681576556"
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/NB_5_Hoodie_6_480x480.png?v=1681595215"
        },
        {
            image:"https://cdn.shopify.com/s/files/1/0744/3968/8473/files/NB_5_Hoodie_7_160x160.png?v=1681595717"
        }
    ]
    return(
        <div>
            {Policy.map((item,i) => (
            <div style={{
                position:"relative",
                bottom:"35vh"
            }}>
                <div style={{
                    position:"relative",
                    fontSize:"2vw",
                    fontWeight:"800"
                }}>
                    {item.header}
                </div>
                <div
                    style={{
                        position:"relative",
                        fontSize:"1.5vw",
                        top:"5vh",
                        left:"5vw",
                        width:"92vw",
                        textAlign:"justify"
                    }}
                >
                    {item.content}
                </div>
                <div>
                    <img style={{
                        position:"relative",
                        float:"left",
                        width:"20vw",
                        height:"20vh"
                    }} src={item.image} />
                </div>
            </div>
            ))}
            <Footer />
        </div>
    )
}

export default Refund;
