import React,{useEffect, useState} from 'react';
import client from '../util/shopify-client'
import './style.css'





const ItemList = () => {
    const [stash, setStash] = useState([]);
    const [source, setSource] = useState([]);
    const [itemId, setItemId]= useState();
    const [currentProduct, setcurrentProduct] = useState([])
    const [showProduct, setShowProduct] = useState(false)


    useEffect(()=> {
    },[])

    client.product.fetchAll().then((products) => {
        setStash(products)       
    }) 


    const HandleProduct = async () => {
        client.product.fetch(itemId).then((product) => {
            setcurrentProduct(product)
        })
    } 

    let myArray = [currentProduct]

    const ChangeProduct = (itemId) => {
        setItemId(itemId)
    }
    const toggle = () => {
        setShowProduct(!showProduct)
        console.log("current item",myArray.slice(-1).pop())
    }

    const ShowProduct = () => {
        return (
            <div className='desContainer'>
                <div> X </div>
                {myArray.map((item,i) => (
                    <div key={i}>
                        <div className='desTitle'> 
                            {item.title}
                        </div>
                        <div style={{
                            position:"relative",
                            width:"55vw",
                            lineHeight:"1.8vw",
                            left:"30vw",
                            fontSize:"1.4vw",
                            bottom:"30vh"
                        }}>
                            {item.description}
                        </div>
                    </div>
                ))}

            </div>
        )
    }


    return (
        <div className='storeContainer'>
            {
                stash.map((item,i) => (
                    <div key={i} className="productContainer">
                        <div key={i}
                            onClick={() => {
                                setItemId(item.id)
                                HandleProduct()
                                toggle()
                            }}
                        >
                            {item.images.map((item,i) => (
                                <div key={i}>
                                    <img src={item.src} className='productImage' />
                                </div>
                            ))}
                        </div>
                        <div className="productTitle">
                            {item.title}
                        </div>
                    </div>
                ))
            }
            {showProduct ? <ShowProduct /> : null }
        </div>
    )
}



const Store = () => {
    return(
        <div>
            <ItemList />
        </div>
    )
}

export default Store;
